import React from "react";
import { films } from "../../data.js";

const listItems = films.map((film) => (
  <li className="film-card" id={film.id}>
    <img className="film-image" src={film.image}></img>
    <a className="film-name">{film.name}</a>
    <a className="film-description">{film.description}</a>
    <a className="film-genre">Genre: {film.genre}</a>
    <a className="film-producer">Producer: {film.producer}</a>
    <div><a className="film-imdb">{film.imdb}</a></div>
  </li>
));

export default function ListItems() {
  return <div>
    <ul className="film-list">
      {listItems}
    </ul>
  </div>;
}
