import React, { useState, useEffect } from "react";
import { films } from "../../data.js";

const ListItems = () => {
  const [filmList, setFilmList] = useState(films);

  useEffect(() => {
    const intervals = filmList.map((film) =>
      setInterval(() => {
        // Yeni bir film listesi oluştur ve sadece ilgili filmin IMDb puanını güncelle
        const updatedList = filmList.map((f) => ({
          ...f,
          imdb: f.id === film.id ? (parseFloat(f.imdb) + (Math.random() < 0.5 ? -0.1 : 0.1)).toFixed(1) : f.imdb,
        }));
        setFilmList(updatedList);
      }, 1000)
    );

    // Component unmount olduğunda tüm intervalleri temizle
    return () => {
      intervals.forEach((interval) => clearInterval(interval));
    };
  }, [filmList]); // filmList değiştiğinde useEffect'i tetikle

  const listItems = filmList.map((film) => (
    <li className="film-card" key={film.id}>
      <img className="film-image" src={film.image} alt={film.name} />
      <p className="film-name">{film.name}</p>
      <p className="film-description">{film.description}</p>
      <p className="film-genre">Genre: {film.genre}</p>
      <p className="film-producer">Producer: {film.producer}</p>
      <div>
        <p className="film-imdb">IMDb: {film.imdb}</p>
      </div>
    </li>
  ));

  return (
    <div>
      <ul className="film-list">{listItems}</ul>
    </div>
  );
};

export default ListItems;
