import React from "react";
import { films } from "../../data.js";

const listItems = films.map((film) => (
  <li className="film-card" id={film.id}>
    <img className="film-image" src={film.image}></img>
    <div className="film-info">
      <div className="imdb">
        <h2 className="film-imdb">{film.imdb}</h2>
      </div>
      <h1 className="film-name">{film.name}</h1>
      <h2 className="film-genre">{film.genre}</h2>
      <h2 className="film-producer">{film.producer}</h2>
      <h2 className="film-description">{film.description}</h2>
    </div>
  </li>
));

export default function ListItems() {
  return <div>
    <ul className="film-list">
      {listItems}
    </ul>
  </div>;
}
